  .filter(([k, v]) => v !== undefined && v !== null && v !== '')
  .map(([k, v]) => `${k}=${encodeURIComponent(v)}`)
  .join('&');

if (queryParams) {
  url += '?' + queryParams;
}

return [{ json: { url, params } }];

